import React, { Component } from 'react'
import { Segment, Form, Input, Button } from 'semantic-ui-react'

class NiceList extends Component {
    state = { name: '', email: '', submittedName: '', submittedEmail: '',  count:0 }

    handleChange = (e, { name, value }) => this.setState({ [name]: value })

    handleSubmit = () => {
        const { name, email } = this.state

        this.setState({ submittedName: name, submittedEmail: email  })
    }
    render() {
        const posCount = () => {
            console.log(this.state.count)
        }
        const negCount = () => {
            console.log(this.state.count)
        }
        const { name, email, submittedName, submittedEmail} = this.state
        return (
        <Segment>
                        <Form onSubmit={this.handleSubmit}>    
                            <h1>Nice List </h1>
                            <Input placeholder='Name' name='name' value={name} onChange={this.handleChange} />
                            <Input
                                placeholder='Email'
                                name='email'
                                value={email}
                                onChange={this.handleChange}
                            />
                            <Button.Group>
                                <Button onClick={this.state.negCount}>-</Button>
                                    <Button.Or text={this.state.count} />
                                <Button positive onClick={posCount}>deux</Button>
                            </Button.Group>
                            <strong>onChange:</strong>
                            <pre>{JSON.stringify({ name, email }, null, 2)}</pre>
                            <strong>onSubmit:</strong>
                            <pre>{JSON.stringify({ submittedName, submittedEmail }, null, 2)}</pre>
                        </Form>
        </Segment>

        )
    }
}

export default NiceList
